import { useState, useEffect } from 'react';
import './App.css';

export const App = () => {
  // Variables
  const [loading, setLoading] = useState(true);
  const [puzzle, setPuzzle] = useState();
  const size = 5;

  useEffect(() => {
    async function fetchData() {
      var puzzleDatabase = require('./db/puzzles.json');

      // console.log(puzzleDatabase.puzzles[0].data)

      setPuzzle(puzzleDatabase.puzzles[0].data)
      setLoading(false);
		}
		fetchData()
	}, []);

  if (loading) {
		return (
			<div className="flex items-center flex-col shadow text-xs bg-white rounded-md">
				<div className="px-6 py-8 items-center">
					Laden....
				</div>
			</div>
		)
	} else {
    return (
      <div className="App">
        <div className='App-body flex justify-center items-center h-screen'>
          <div className='p-5 bg-white shadow-2xl rounded-sm'>
            <div className="flex flex-col">
              <div className="overflow-x-auto sm:-mx-6 lg:-mx-8">
                <div className="inline-block min-w-full py-2 sm:px-6 lg:px-8">
                  <div className="overflow-hidden grid grid-cols-5 grid-rows-5">
                    {Array.from({ length: size * size }).map((_, index) => {
                        const row = Math.floor(index / size);
                        const col = index % size;

                        return (
                          <div className="p-1 border" key={`${row}-${col}`}>
                            {puzzle[row][col]}
                          </div>
                        );
                      })}
                  </div>
                </div>
              </div>
            </div>
            </div>
        </div>
      </div>
    );
  }
}

export default App;
